// javac -d . JUtil.java// Deitel 344import java.io.*;// #include <???.h>//Uses crt;import java.text.DecimalFormat;  // Deitel 168public class JUtil { // Nome da Classe	public static String readString () throws Exception {		DataInputStream A = new DataInputStream (System.in);		return (A.readLine ());	}	public static byte readByte () throws Exception  {		return (Byte.valueOf (readString ()).byteValue());	}	public static short readShort () throws Exception  {		return (Short.valueOf (readString ()).shortValue());	}	public static int readInt ()  throws Exception {		return (Integer.valueOf (readString ()).intValue());	}	public static float readFloat () throws Exception {		return (Float.valueOf (readString ()).floatValue());	}   	public static double readDouble () throws Exception {		return ( Double.valueOf (readString ()).doubleValue() );	}   	public static char readChar () throws Exception {		return ( readString ().charAt (0) );	}	public static void pause () throws Exception {		String a = readString ();}	public static String picture (double num,String formato) {	return ((new DecimalFormat (formato)).format (num));	}	public static String pictureNumero (double num) {	return (picture (num,"#,###,##0.00"));	}	public static String pictureMoeda (double num, String moeda) {	return (picture (num,moeda + " #,###,##0.00"));	}	public static String pictureReal (double num) {	return (pictureMoeda (num,"R$"));	}	public static String pictureDolar (double num) {	return (pictureMoeda (num,"US$"));	}}